class MapElement {
    String key;
    int value;
    MapElement(String key, int value) {
        this.key = key;
        this.value = value;
    }
}

class MapElementComparator implements Comparator<MapElement> {
    @Override
    public int compare(MapElement me1, MapElement me2) {
        if(me1.value != me2.value) {
            return me1.value - me2.value;
        }
        return me1.key.compareTo(me2.key);
    }
}

class Solution {
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        HashMap<String,Integer> freqMap = new HashMap<>();
        boolean[] visited = new boolean[watchedVideos.size()];
        Queue<Integer> queue = new LinkedList<>();
        queue.offer(id);
        visited[id] = true;
        while(level >= 0 && !queue.isEmpty()) {
            int size = queue.size();
            while(size-- > 0) {
                int current = queue.poll();
                for(int friend: friends[current]) {
                    if(visited[friend]) {
                        continue;
                    }
                    queue.offer(friend);
                    visited[friend] = true;
                }
                if(level == 0) {
                    for(String video: watchedVideos.get(current)) {
                        freqMap.put(video, freqMap.getOrDefault(video, 0) + 1);
                    }
                }
            }
            level--;
        }
        PriorityQueue<MapElement> priorityQueue = new PriorityQueue<>(new MapElementComparator());
        for(String key: freqMap.keySet()) {
            priorityQueue.offer(new MapElement(key, freqMap.get(key)));
        }
        List<String> answer = new ArrayList<>();
        while(!priorityQueue.isEmpty()) {
            answer.add(priorityQueue.poll().key);
        }
        return answer;
    }
}
