class Solution {
public:

    /*
        So if we observe carefully this is a standard BFS algorithm question as here we need to find the k level friends of ours.
        This can be easily achieved by BFS algorithm.

        So what we do is we run BFS algorithm for level no of times and get the friends which are at distance k from us and from here only we need to find their most watched videos.

        so we simply use a hash map to store the movies watched by our k level friends and its frequecy because in answer we need to sort them according to the frequency of the watched videos.

        after that we take a vector and sort it according to the frequency of watched videos. and last we return the result.

        Please Upvote if you like the solution.
        for any changes please comment.
    */

    vector<string> watchedVideosByFriends(vector<vector<string>>& watchedVideos, vector<vector<int>>& friends, int id, int level) {
        int n=friends.size();
        queue<int> q;
        vector<bool> visited(n,false);
        q.push(id);
        visited[id]=true;
        while(level--)
        {
            int size=q.size();
            while(size--)
            {
                int cur=q.front();
                q.pop();
                for(auto child:friends[cur])
                {
                    if(!visited[child])
                    {
                        visited[child]=true;
                        q.push(child);
                    }
                }
            }
        }
        unordered_map<string,int> mp;
        while(!q.empty())
        {
            vector<string> temp=watchedVideos[q.front()];
            q.pop();
            for(auto i:temp)
                mp[i]++;
        }
        vector<pair<int,string> > res;
        for(auto i:mp)
            res.push_back({i.second,i.first});
        sort(res.begin(),res.end());
        vector<string> ans;
        for(auto i:res)
            ans.push_back(i.second);
        return ans;
    }
};

