}

func (ms *MovieSorter) Len() int {
    return len(ms.movies)
}

func (ms *MovieSorter) Less(i, j int) bool {
    if ms.movies[i].cnt != ms.movies[j].cnt {
        return ms.movies[i].cnt < ms.movies[j].cnt
    }
    return ms.movies[i].movie < ms.movies[j].movie
}

func (ms *MovieSorter) Swap(i, j int) {
    ms.movies[i], ms.movies[j] = ms.movies[j], ms.movies[i]
}

