    adjList := make([][]int, n)
    for i:=0; i<n; i++ {
        adjList[i] = make([]int, 0)
    }
    return &FriendsGraph{
        SelfId:     sid,
        AdjList:    adjList,
        N:          n,
        Level:      level,
    }
}

func (g *FriendsGraph) AddEdge(src int, dest int) {
