        default:
        panic("write block")
    }
}

func (g *FriendsGraph) GrabAtLevel() []int {
    q := make(chan QElem, 100000)
    visitedMap := make(map[int]bool)
    writeTo(q, QElem{Id: g.SelfId, Dist: 0})
    grabbed := make(map[int]bool)
    for len(q) > 0 {
        current := readFrom(q)
        _, ok := visitedMap[current.Id]
        if ok {
            continue
        }
        visitedMap[current.Id] = true
        if current.Dist > g.Level {
            continue
        } else if current.Dist == g.Level {
            grabbed[current.Id] = true
            continue
        } else {
            // Iterate through neighbours.
            for _, neighbour := range g.AdjList[current.Id] {
                _, ok := visitedMap[neighbour]
                if !ok {
                    writeTo(q, QElem{Id: neighbour, Dist: current.Dist+1})
                }
            }
        }
    }
    result := make([]int, 0)
    for k, _ := range grabbed {
