class Solution {
public:
    vector<string> watchedVideosByFriends(vector<vector<string>>& w, vector<vector<int>>& friends, int id, int level) {
        int nodes=friends.size();
        vector<int> vis(nodes,0);
        int lev=0;
        map<string,int> m;
        queue<int> q;
        q.push(id);
        while(!q.empty()){
            lev++;
            int s=q.size();
            for(int i=0;i<s;i++){
                int n=q.front();
                q.pop();
                vis[n]=1;
               for(auto it: friends[n]){
                   if(!vis[it]){
                       if(lev==level){       //if level is equal to lev then we push all the movies watched by friends at level lev into the map
                           for(auto x:w[it]){  
                               m[x]++;    
                           } 
                       }
                       q.push(it); 
                   }
                   vis[it]=1;
               }
            }
        }

        multimap<int, string> MM;   //we use multimap here to sort the movies alphabetically and frequency wise
        vector<string> ans;
        for (auto& it : m) {
            MM.insert({ it.second, it.first });
        }

        for (auto& it : MM) {
          ans.push_back(it.second);
         }
        return ans;

    }
};
