class Solution {
public:
    vector<string> watchedVideosByFriends(vector<vector<string>>& watchedVideos, vector<vector<int>>& friends, int id, int level) {
        int n = friends.size();
        vector<int> vis(n, 0);
        int lvl=0;
        set<int> q;
        q.insert(id);
        vis[id]=1;
        while(lvl!=level and !q.empty()){
            set<int> tmp;
            for(auto a: q){
                for(auto i: friends[a]){
                    if(vis[i]==0){
                        tmp.insert(i);
                        vis[i]=1;
                    }
                }
            }
            q = tmp;
            lvl++;
        }
        map<string, int> mp;
        for(auto a: q){
            for(auto video: watchedVideos[a]){
                mp[video]++;
            }
        }
        vector<pair<int,string>> l;
        for(auto [video, cnt]: mp){
            l.push_back({cnt, video});
        }
        sort(l.begin(), l.end());
        vector<string> ans;
        for(auto a: l){
            ans.push_back(a.second);
        }
        return ans;
    }
};
