class Solution {
    static class Pair
    {
        String key;
        int value;
        Pair(String key,int value)
        {
            this.key=key;
            this.value=value;
        }
    }
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        int n=watchedVideos.size();
        HashMap<String,Integer> freq=new HashMap<>();
        boolean vis[]=new boolean[n];
        Queue<Integer> queue=new LinkedList<>();
        queue.offer(id);
        vis[id]=true;
        while(level>=0&&!queue.isEmpty())
        {
            int size=queue.size();
            while(size-->0)
            {
                int curr=queue.poll();
                for(int friend:friends[curr])
                {
                    if(vis[friend])
                        continue;
                    queue.add(friend);
                    vis[friend]=true;
                }
                if(level==0)
                {
                    for(String video:watchedVideos.get(curr))
                        freq.put(video,freq.getOrDefault(video,0)+1);
                }
            }
            --level;

        }
        PriorityQueue<Pair> pQueue=new PriorityQueue<>((a,b)->{
            if(a.value!=b.value)
                return a.value-b.value;
            return a.key.compareTo(b.key);
        });
        for(String key:freq.keySet())
        {
            int times=freq.get(key);
            pQueue.add(new Pair(key,times));
        }
        List<String> ans=new LinkedList<>();
        while(!pQueue.isEmpty())
            ans.add(pQueue.poll().key);
        return ans;
    }
}

