```c#
public class Solution {
    private HashSet<int> seenFriends;
    private HashSet<int> levelFriends;

    public IList<string> WatchedVideosByFriends(IList<IList<string>> watchedVideos, int[][] friends, int id, int level) {
        // we start at level 0.
        // maintain two sets: one for friends we have seen ever (including at previous levels)
        // and one for friends that are always at this level.
        seenFriends = new HashSet<int>(new [] { id });
        levelFriends = new HashSet<int>(new [] { id });

        // figure out our friends at the given level...
        for (var index = 0; index < level; index++) {
            var nextLevelFriends = new HashSet<int>();
            foreach (var friend in levelFriends) {

                // we want all friends of friends from the previous level that we haven't
                // already found at a prior level.
                foreach (var nextFriend in friends[friend]) {
                    if (seenFriends.Add(nextFriend)) {
                        nextLevelFriends.Add(nextFriend);
                    }
                }
            }
            levelFriends = nextLevelFriends;
        }

        // LINQ is great.  Long live LINQ.
        return levelFriends
            .SelectMany(friend => watchedVideos[friend])
            .GroupBy(video => video)
            .OrderBy(g => g.Count())
            .ThenBy(g => g.Key)
            .Select(g => g.Key)
            .ToList();
    }
}
