	class Solution {
    class VideoCount
    {
        String v;
        int count;

        VideoCount(String v, int count)
        {
            this.v = v;
            this.count = count;
        }

        @Override
        public String toString()
        {
            return this.v+" "+this.count;
        }
    }
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        boolean[] visited = new boolean[watchedVideos.size()];
        Queue<Integer> queue = new LinkedList<>();
        queue.add(id);
        visited[id] = true;
        int currLevel = 0;

        while(!queue.isEmpty())
        {
            int size = queue.size();
            while(size-- > 0)
            {
                int node = queue.poll();
                int[] nei = friends[node];
                for(int i : nei)
                {
                    if(!visited[i])
                    {
                        queue.add(i);
                        visited[i] = true;
                    }
                }
            }

            currLevel++;

            if(currLevel == level)
            {
                break;
            }
        }

        Map<String,VideoCount> map = new HashMap<>();
        while(!queue.isEmpty())
        {
            Integer f = queue.poll();
            List<String> watchedVideo = watchedVideos.get(f);
            for(String video : watchedVideo)
            {
                map.putIfAbsent(video, new VideoCount(video,0));
                map.get(video).count++;
            }
        }

        PriorityQueue<VideoCount> pq = new PriorityQueue<>((v1,v2) -> (v1.count == v2.count) ? 
                                                           v1.v.compareTo(v2.v) :
                                                           v1.count-v2.count);
        for(String key : map.keySet())
        {
            pq.add(map.get(key));
        }

        List<String> res = new ArrayList<>();
        while(!pq.isEmpty())
        {
            res.add(pq.poll().v);
        }

        return res;
    }
	}

