class Solution {
public:
set<int> v;
void dfs( unordered_map<string, int>& dict, vector<vector<string>> videos, vector<vector<int>> g, int& d, int level, int id){

     if( d == level ){

         for( auto& n: g[id]){
            if( v.count(n) != 0 ) continue;
            v.insert(n);
            for( auto& s: videos[n]){
                dict[s]++;
            }
         }
         return;
     }

     if( d < level ){
         d++;
         for( auto& n: g[id]){
            v.insert(n);
         }

         for( auto& n: g[id]){
            dfs( dict, videos, g, d,level, n);
         }
     } 
}

