The Plan: 
1. Find friends with shortest path distance of `level` using BFS (we can use BFS for shortest path because it's undirected unweighted graph)
2. Get all watched videos by the friends at `level` and sort

```
fun watchedVideosByFriends(
        watchedVideos: List<List<String>>,
        friends: Array<IntArray>,
        id: Int,
		level: Int
    ): List<String> {
        if (id + level > friends.size) {
            return emptyList()
        }

        val visited = mutableSetOf(id)
        val queue = ArrayDeque<Int>()

        queue.offer(id)
        var currentLevel = 0

        while(queue.isNotEmpty() && currentLevel  < level) {
            val levelSize = queue.size

            for (i in 1..levelSize) {
                val friend = queue.poll()

                for (adj in friends[friend]) {
                    if (!visited.contains(adj)) {
                        queue.offer(adj)
                        visited.add(adj)
                    }
                }
            }

            currentLevel++
        }

        return queue
            .asSequence()
            .flatMap {
                watchedVideos[it].asSequence()
            }
            .groupBy { it }
            .map {
                it.key to it.value.size
            }
            .sortedWith(
                compareBy(
                    { it.second },
                    { it.first }
                )
            )
            .map {
                it.first
            }
    }
