from collections import deque,defaultdict
class Solution:
    def watchedVideosByFriends(self, watchedVideos, friends, id: int, level: int) :
        ans=[]
        d=defaultdict(int)
        if level==0:
            return watchedVideos[id]
        q=deque()
        visit=[id]
        for f in friends[id]:
            q.append((f,1))
            visit.append(f)

        while q:
            i,l=q.popleft()
            if l==level:
                for w in watchedVideos[i]:
                    d[w]+=1
            else:
                for f in friends[i]:
                    if f not in visit:
                        visit.append(f)
                        q.append((f,l+1))
        return [k for k,v in sorted(d.items(),key=lambda item:(item[1],item[0]))]
