class Solution {
    func watchedVideosByFriends(
        _ watchedVideos: [[String]], 
        _ friends: [[Int]], 
        _ id: Int, 
        _ level: Int
    ) -> [String] {

        /*
         BFS to find IDs of all users at the given relation level
        */
        var queue = [Int]()        
        var visited = Set<Int>() 

        queue.append(id)
        visited.insert(id)

        var users = [Int]()
        var currentLevel: Int = 1

        while !queue.isEmpty {
            for _ in 0..<queue.count {
                let userID = queue.removeFirst()       
                let userFriends = friends[userID]      
                for friend in userFriends {     
                    if !visited.contains(friend) {
                        queue.append(friend)
                        visited.insert(friend)
                    }
                }
            }
            if currentLevel == level {
                users = queue
                break
            }
            currentLevel += 1
        }

        /*
         Get all watched videos and determine their frequency
        */
        var videoNames = [String]()
        var videoFreqs = [String: Int]()
        for user in users {
            let vids = watchedVideos[user]
            for vid in vids {
                if videoFreqs[vid] != nil {
                    videoFreqs[vid]! += 1
                } else {
                    videoFreqs[vid] = 1
                    videoNames.append(vid)
                }
            }
        }

        /* 
         Return list of watched videos sorted alphabetically and by frequency
        */
        videoNames.sort(by: <)
        videoNames.sort(by: { videoFreqs[$0]! < videoFreqs[$1]! })
        return videoNames

    }
}
