/**
 * @param {string[][]} watchedVideos
 * @param {number[][]} friends
 * @param {number} id
 * @param {number} level
 * @return {string[]}
 */
var watchedVideosByFriends = function(watchedVideos, friends, id, level) {
    function Node(val) {
        this.val = val
        this.neighs = new Set()
        this.visited = false
        this.videos = []
        Node.map.set(val, this)
    }
    Node.map = new Map()
    Node.get = val => Node.map.get(val)

    const len = watchedVideos.length
    for (let i = 0; i < len; i++) {
        const node = new Node(i)
        const videoList = watchedVideos[i]
        node.videos = videoList
    }

    for (const [i, friendList] of friends.entries()) {
        const fromNode = Node.get(i)
        for (const friend of friendList.values()) {
            const toNode = Node.get(friend)
            fromNode.neighs.add(toNode)
            toNode.neighs.add(fromNode)
        }
    }


    const me = Node.get(id)
    me.visited = true
    let depth = 0
    let queue = [me, null]

    // video id => freq of video id
    const map = new Map()
    while (queue.length) {
        const head = queue.shift()
        if (head) {
            for (const neigh of head.neighs) {
                if (!neigh.visited) {
                    neigh.visited = true
                    queue.push(neigh)
                }
            }
            if (depth === level) {
                const videos = head.videos
                for (const video of videos.values()) {
                    map.set(video, 1 + (map.get(video) || 0))
                }
            } else if (depth > level) {
                break
            }
        } else {
            if (queue.length)   queue.push(null)
            depth++
        }
    }


    let result = []
    for (const [video, freq] of map.entries()) {
        result.push({ video, freq })
    }
    result.sort((A, B) => (A.freq - B.freq) || A.video.localeCompare(B.video))
    result = result.map(A => A.video)


    return result
};
