class Solution {
public:
        //using bfs will be a better choice to select short path 
    //as dfs will reach a node earlier with some great level which could have been visited later on with some lower level 
    vector<string> watchedVideosByFriends(vector<vector<string>>& wv, vector<vector<int>>& f, int id, int level) {
        unordered_map<string,int> mp;
        int l=-1;int n=f.size();
        vector<int> vis(n,0);
        queue<int> que;
        que.push(id);
        vis[id]=1;
        while(!que.empty()){
            l++;
            int k=que.size();
            while(k){
                k--;
                int u=que.front();
                que.pop();
                if(l==level){
                    for(auto &x:wv[u])mp[x]++;
                }
                for(int &v:f[u]){
                    if(!vis[v]){
                        que.push(v);
                        vis[v]=1;
                    }
                }
            }

        }
        set<pair<int,string>> st;
        for(auto &x:mp){
            st.insert({x.second,x.first});
            //cout<<x.first<<" "<<x.second<<"\n";
        }
        vector<string> ans;
        for(auto &x:st){
            ans.push_back(x.second);
        }
        return ans;

    }
};
