class Solution {
public:
    vector<string> watchedVideosByFriends(vector<vector<string>>& watchedVideos, vector<vector<int>>& friends, int id, int level) {
        vector<vector<int>> G(friends.size(),vector<int>());
        vector<int> seq;
        vector<int> visit(friends.size(),0);
        queue<int> q;
        for(int i=0;i<friends.size();++i){
            for(auto &f:friends[i])
                G[i].push_back(f);
        }
        q.push(id);
        q.push(-1);
        visit[id]=1;
        unordered_map<string,int> cnt;
        while(q.size() && level){
            int n=q.front();
            q.pop();

            if(n==-1){
                --level;
                if(level)
                    q.push(-1);
                continue;
            }
            for(auto &f:G[n]){
                if(visit[f]) 
                    continue;
                if(level==1)//the next layer is the target
                    for(auto &v:watchedVideos[f]){
                        ++cnt[v];
                }
                visit[f]=1;
                q.push(f);
            }
        }
        vector<pair<string,int>> tmp;
        for(auto &r:cnt){
            tmp.push_back(r);
        }
        sort(tmp.begin(),tmp.end(),[](auto& a,auto &b){
            if(a.second==b.second)
                return a.first<b.first;
            else
                return a.second<b.second;
        });
        vector<string> ans;
        for(auto &r:tmp){
            ans.push_back(r.first);
        }
        return ans;

    }
};
