```
/*
greedily take the largest possible value for the curr Index as we build the array from left to right because 
it is always beneficial to choose a larger number in the most signifant pos to create lexicigraphically larger array 
and check if we can create the valid array by backtracking with given constraints . 
keep a boolean flag to check if we have already created an  array so we can terminate all the rest of recursive 
calls once we get our largest ans . 
*/

class Solution {
    private static boolean done ; // flag for checking if we already have our ans or not 
    private static void backtrack(boolean placed[] ,int l , int n , int temp[] ,int [] ans){
        if(done == true)
            return ;
        if(l == temp.length){
            done = true ;
            System.arraycopy(temp , 0 , ans , 0 , temp.length);
            return ;
        }
        // if currIndex is already set move to next Index
        if(temp[l] != 0)
            backtrack(placed , l + 1 , n , temp ,ans);
        else{
            for(int i = n ; i > 0 ; i--){
                if(placed[i] == false && i > 1 && l + i < temp.length && temp[l + i] == 0){
                    setValue(temp , l , l + i , i , placed , i , true);
                    backtrack(placed , l + 1 , n , temp , ans);
                    setValue(temp , l , l + i , 0 , placed , i , false);
                }
                else if(placed[i] == false && i == 1){
                    setValue(temp , l , l , i , placed , i , true);
                    backtrack(placed , l + 1 , n , temp , ans);
                    setValue(temp , l , l , 0 , placed , i , false);
                }
            } 
        }
    }
    public int[] constructDistancedSequence(int n) {
        int temp[] = new int[2*n - 1] , ans[] = new int[2*n - 1];;
        boolean placed[] = new boolean[n + 1];
        done = false;
        backtrack(placed , 0 , n , temp , ans);
        return ans ;
    }
    private static void setValue(int nums[],int i ,int j , int val , boolean p[] ,int idx, boolean state){
        nums[i] = val ;
        nums[j] = val;
        p[idx] = state;
    }
}
