The main idea is to check which maximum value we can put at the current index, we put it and then call recursion for the next index. If it returns true then we have got the answer. Else we will backtrack and set other large value on current index and do the same thing.

```
class Solution {
public:

    bool recurse(int index, vector<bool> &visited, vector<int> &arr, int &n, int left)
    {
        if(left == 0)
            return true;
        if(arr[index] != -1)
            return recurse(index + 1, visited, arr, n, left);
        for(int i=n;i>=1;i--)
        {
            if(visited[i])
                continue;
            if(i == 1)
            {
                arr[index] = 1;
                visited[i] = true;
                if(recurse(index + 1, visited, arr, n, left-1))
                {
                    return true;   
                }
                arr[index] = -1;
                visited[i] = false;
            }
            else
            {
                if(index + i >= arr.size() || arr[index+i] != -1)
                    continue;
                arr[index] = i;
                arr[index+i] = i;
                visited[i] = true;
                if(recurse(index + 1, visited, arr, n, left-2))
                {
                    return true;   
                }
                arr[index] = -1;
                arr[index+i] = -1;
                visited[i] = false;
            }
        }
        return false;
    }

    vector<int> constructDistancedSequence(int n) 
    {
        vector<int> arr(2*n-1, -1);        
        vector<bool> visited(n+1, false);
        visited[0] = true;
        recurse(0, visited, arr, n, 2*n-1);
        return arr;
    }
};
```

