func constructDistancedSequence(n int) []int {
	s := make([]bool, n+1)
	ll := (n * 2) - 2
	pi := make([]int, ll+1)
	var bt func(int) bool
	bt = func(c int) bool {
		if c > ll { return true }
		i := 0
		for j := range pi { if pi[j] == 0 { i = j; break } }
		for nx := n; nx > 0; nx-- {
			if nx != 1 {
				if (i+nx) > ll || pi[i] != 0 || pi[i+nx] != 0 || s[nx] { continue }
				pi[i] = nx
				pi[i+nx] = nx
				s[nx] = true
				if bt(c + 2) { return true }
				pi[i] = 0
				pi[i+nx] = 0
				s[nx] = false
			} else {
				if pi[i] != 0 || s[nx] { continue }
				pi[i] = nx
				s[nx] = true
				if bt(c + 1) { return true }
				pi[i] = 0
				s[nx] = false
			}
		}
		return false
	}
	bt(0)
	return pi
}
