class Solution {
    public int[] constructDistancedSequence(int n) {
        int[] ans = new int[2 * n - 1];
        backtrack(0, ans, new boolean[n + 1]);
        return ans;
    }

    private boolean backtrack(int p, int[] ans, boolean[] used) {
        if (p == ans.length) return true;
        if (ans[p] != 0) return backtrack(p + 1, ans, used);

        for (int i = (ans.length + 1) / 2; i >= 1; i--) {
            if (used[i]) continue;
            if (i > 1) {
                if (p + i >= ans.length || ans[p + i] != 0) continue;
                ans[p] = i;
                ans[p + i] = i;
                used[i] = true;
                if (backtrack(p + 1, ans, used)) return true;
                ans[p] = 0;
                ans[p + i] = 0;
                used[i] = false;
            } else {
                ans[p] = i;
                used[i] = true;
                if (backtrack(p + 1, ans, used)) return true;
                ans[p] = 0;
                used[i] = false;
            }
        }

        return false;
    }
}
