We start from n and break the recusion as soon as we meet the solution to get lexigrapgically largest answer

class Solution {
public:
    unordered_map<int,int> h;
    vector<int> constructDistancedSequence(int n) {
        vector<int> v((2*n)-1,0);
        int i;
        backtrack(v,0,n);
        return v;
    }

    bool backtrack(vector<int> &v,int p,int n)
    {
        if(p==v.size())
        return true;

        else if(v[p]!=0)
        return backtrack(v,p+1,n);

        else
        {       
            for(int i=n;i>=1;i--)
            {
                if(h[i]>0)
                continue;

                if(i==1)
                {
                v[p]=1;
                h[1]++;
                if(backtrack(v,p+1,n))
                return true;
                v[p]=0;
                h[1]--;
                }

                else if(p+i<v.size() && v[p]==0 && v[p+i]==0 )
                {
                v[p]=i;
                v[p+i]=i;
                h[i]++;
                if(backtrack(v,p+1,n))
                return true;
                h[i]--;
                v[p]=0;
                v[p+i]=0;
                }
            }  
        }  
        return false;
    }

};

