class Solution {
  bool construct_seq(size_t pos, vector<int> &seq, vector<bool> &used) {
    if (pos == seq.size()) return 1;
    if (seq[pos]) return construct_seq(pos+1, seq, used);

    for (int i = used.size()-1; i > 0; i--) {
      if (used[i]) continue;
      if (i == 1 || (seq.size() > pos + i && seq[pos + i] == 0)) {
        used[i] = seq[pos] = seq[pos + (i > 1? i : 0)] = i;
        if (construct_seq(pos + 1, seq, used)) return 1;
        else used[i] = seq[pos] = seq[pos + (i > 1? i : 0)] = 0;
      }
    }
    return 0;
  }

public:
  // Complexity: O(n!) in the worst case
  vector<int> constructDistancedSequence(int n) {
    vector<bool> used(n+1);
    vector<int> seq((n-1)*2 + 1);
    construct_seq(0, seq, used);
    return seq;
  }
};
