class Solution
{

    vector<bool> visited;
public:

    bool backtrack(int idx,int n,vector<int> &a)
    {
         // if we reached end ,it means we have found the solution
         if(idx == a.size())
         return true;

        // if current index is already filled, then just move on
        if(a[idx] != 0)
        return  backtrack(idx + 1, n, a);

        //now try to fill curr position greedily
        //ie. starting from highest no to lowest
        for(int i = n; i >= 1; i--)
        {
            //checking if it is already used
            if(visited[i])
                continue;

            if(i == 1)
            {
                a[idx] = 1;
                visited[1] = true;

                if(backtrack(idx+1,n,a)) return true;

                a[idx] = 0;
                visited[1] = false;

            }

            else if(a[idx] == 0 and i + idx < a.size() and a[i + idx] == 0)
            {
                // temporaily filling the values
                a[idx] = i;
                a[i+idx] = i;
                visited[i] = true;

                if(backtrack(idx+1,n,a)) return true;

                // resetting the values
                visited[i] = false;
                a[idx] = 0;
                a[i + idx] = 0;
            }
        }

        return false;
    }
    vector<int> constructDistancedSequence(int n)
    {
        // base case
        if(n == 1)
            return {1};

        vector<int> a(2*n - 1);
        visited = vector<bool>(n);

        // trivial case
        a[0] = a[n] = n;

        if(!backtrack(1,n-1,a))
            return {0};

        return a;
    }
};

