class Solution {
public:

    int sz;
    unordered_set<int> vis;

    bool solve(int id,vector<int> &arr,int n){

        if(id==sz){
            if(vis.size()!=n) return 0;
            return 1;
        }

        if(arr[id]!=0) 
            return solve(id+1,arr,n);

        for(int i=n;i>=1;i--){

            if(vis.count(i)) continue;

            if(i!=1 && id+i<sz && arr[id+i]==0){
                arr[id] = arr[id+i] = i;
                vis.insert(i);
                if(solve(id+1,arr,n)) return 1;
                arr[id] = arr[id+i] = 0;
                vis.erase(i);
            }
