class Solution {
public:
    vector<int> rec;
    vector<vector<int>> all;
    bool visit[21];
    vector<int> constructDistancedSequence(int n) {
        rec.resize(2 * n - 1, 0);
        memset(visit, false, sizeof visit);
        dfs(n, 0);
        return rec;
    }
    bool dfs(int n, int i) {
        while (i < 2 * n - 1 && rec[i] != 0) i++;
        if (i == 2 * n - 1) return true;
        for (int j = n; j > 1; j--) {
            if (!visit[j] && i + j < 2 * n - 1 && rec[i + j] == 0) {
                visit[j] = true;
