```Scala
import scala.collection.immutable.TreeSet
object Solution {
    def constructDistancedSequence(n: Int): Array[Int] = {
        def go(cur: Vector[Int], i: Int, left: TreeSet[Int]): Option[Vector[Int]] = {
            if (i == (2 * n - 1))
                Some(cur)
            else if (cur(i) != 0)
                go(cur, i + 1, left)
            else
                left.iterator.map {
                    case 1 =>
                        go(cur.updated(i, 1), i + 1, left - 1)
                    case x if (cur.lift(i + x).getOrElse(1) == 0) =>
                        go(cur.updated(i, x).updated(i + x, x), i + 1, left - x)
                    case _ => None
                }.find(_.isDefined).getOrElse(None)
        }

        go(Vector.fill(2 * n - 1)(0), 0, TreeSet[Int]()(Ordering.by(-_)) ++ (1 to n)).get.toArray
    }
}
