class Solution {
public:
    vector<int> ans ;
    int sz ;

    void solve(int pos , int n , vector<int>&temp){
        if(pos > n){
            if(ans < temp) ans = temp ; // takes O(N) 
            return ;
        }

        for(int i = 0 ; i < sz ; ++i ){
            if(temp[i]) continue ;
            if(pos == 1){
                temp[i] = pos ;
                solve(pos+1,n,temp);
                temp[i] = 0 ;
            } else {
                if(i + pos < sz and !temp[i + pos]){
                    temp[i] = pos ;
                    temp[i+pos] = pos ;
                    solve(pos + 1,n,temp) ;
                    temp[i] = 0 ;
                    temp[i+pos] = 0 ;
                }
            }
        }
        return ;

    }
    vector<int> constructDistancedSequence(int n) {
        sz = 2*n - 1 ;
        ans.resize(sz,0);
        vector<int>temp(sz,0) ;
        solve(1,n,temp) ;
        return ans ;
    }
};
