class Solution {
public:
    int sz ;
    vector<int> ans , numberTaken ;

    bool solve(int pos , int n){
        if(pos >= sz) return true ; //if all positions are filled the formed array is valid 
        if(ans[pos]) return solve(pos + 1 , n) ; //if current space is occupied move to next position 

        //Greedy nature , start filling from largest number first 
        for(int number =  n ; number >= 1 ; --number ){
            if(numberTaken[number]) continue ;
            ans[pos] = number ;
            numberTaken[number] = 1 ;
            if(number == 1){
                if(solve(pos + 1, n)) return true ;
            } else {
                if(pos + number < sz and !ans[pos + number]){
                    ans[pos + number] = number ;
                    if(solve(pos + 1 ,n)) return true ;
                    ans[pos + number] = 0 ;
                }
            }
            ans[pos] = 0 ;
            numberTaken[number] = 0 ;
        }
        return false ;

    }

    vector<int> constructDistancedSequence(int n) {
        sz = 2*n - 1 ;
        ans.resize(sz,0) , numberTaken.resize(n + 1,0) ;
        solve(0,n) ;
        return ans ;
    }
};
