class Solution {
public:
    bool dfs(vector<int>&ans, vector<int>&seq, vector<bool>&visited, int current){
        int i;
        if(current == seq.size()){
            ans = seq;
            return true;
        }

        if(seq[current] != -1){
            return dfs(ans, seq, visited, current + 1);
        }

        bool result = false;
        for(i = visited.size() - 1; i >= 2; i--){
            if(visited[i]){
                continue;
            }
            if(current + i < seq.size() && seq[current + i] == -1){
                seq[current] = i;
                seq[current + i] = i;
                visited[i] = true;
                result = dfs(ans, seq, visited, current + 1);
                visited[i] = false;
                seq[current + i] = -1;
                seq[current] = -1;
            }
            if(result){
                return true;
            }
        }
        if(!visited[1]){
            visited[1] = true;
            seq[current] = 1;
            result = dfs(ans, seq, visited, current + 1);
            seq[current] = -1;
            visited[1] = false;
        }
        return result;
    }

