//if list toPlace is empty
    //we are done
//else
    //at each step take the largest available number
    //if > 1
        //place at next available idx and at idx + i
    //if == 1
        //place 1 at next available idx
    //if success
        //remove i from list to place and continue
    //if failure backtrack and try next largest number
        //add number back to list
        //set values back to 0

public class Solution 
{
    public int[] ConstructDistancedSequence(int n) 
    {
        var output = new int[((n - 1)*2) + 1];
        var toPlace = Enumerable.Range(1, n).ToList();

        PlaceNextGoodNumber(0, toPlace, output);

        return output;
    }

    private bool PlaceNextGoodNumber(int idx, List<int> toPlace, int[] output)
    {
        bool isConstructed = false;

        //backtrack
        if (toPlace.Count == 0)
        {
            return true;
        }
        else
        {
            //get the count since the list will be modified throughout the loop
            var count = toPlace.Count;

            for (int i = count - 1; i >= 0; i--)
            {
                //get the biggest number we have not tried yet
                var num = toPlace[i];

                if (num == 1 || IsValidPlacement(num, idx, output))
                {
                    //place the number in the correct spot(s)
                    output[idx] = num;

                    if (num != 1)
                    {
                        output[idx + num] = num;                        
                    }

                    toPlace.RemoveAt(i);

                    isConstructed = PlaceNextGoodNumber(GetNextIdx(output, idx + 1), toPlace, output);

                    //reverse the changes
                    if (!isConstructed)
                    {
                        toPlace.Insert(i, num);

                        output[idx] = 0;

                        if (num != 1)
                        {
                            output[idx + num] = 0;                        
                        }
                    }
                    //we have constructed the largest valid sequence 
                    //therefore do not undo the changes made, just return
                    else
                    {
                        break;
                    }
                }
            }
        }

        return isConstructed;
    }

    private bool IsValidPlacement(int num, int idx, int[] output)
    {
        //ensure the number can placed i spaces apart &&
        //that the space is within the length of the output[]
        if (num + idx >= output.Length || output[num + idx] != 0)
        {
            return false;    
        }

        return true;
    }

    private int GetNextIdx(int[] output, int startIdx)
    {
        var nextIdx = -1;

        for (int i = startIdx; i < output.Length; i++)
        {
            if (output[i] == 0)
            {
                return i;
            }
        }

        //-1 can be returned when the output[] is full
        //but in that case the backtracking will prevent IOOBE
        return 1;
    }
}
