const constructDistancedSequence = function (number) {
    // declare the sequence array with length
    const sequence = new Array(number*2-1);

    const dfs = function(depth) {
        // base condition
        if(depth === sequence.length) return true; // since we are at the max depth, problem is solved

        if(sequence[depth]) return dfs(depth + 1); // if depth has already been evaluated move on to the next one.

        for(let trial=number; trial > 0; trial--) { // try for each number from max to 1 and see if they can fit into the sequence
            if(sequence.includes(trial)) continue; // we already have this trial in the sequence move on to the next
            if(trial === 1) { //special handling for the 1
                sequence[depth] = trial; // assaign trail to the empty space
                if(dfs(depth + 1)) return true; // since next depth can be evaluted do nothing
                sequence[depth] = 0; // since next depth is not able to evaluate redo this step
            }else if(depth + trial < sequence.length && !sequence[depth + trial]) {
                // since trial can fit, assign and hold trial in sequence array
                sequence[depth] = trial;
                sequence[depth + trial] = trial;
                if(dfs(depth + 1)) return true; // since next depth can be evaluated return true and do nothing.
                // since next depth can't fit, redo the option
                sequence[depth] = 0;
                sequence[depth + trial] = 0;
            }
        }
    }
    // start with the root depth
    dfs(0);
    return sequence;
}
