class Solution {
public:
bool help(vector<int> &ans,int pos,int mask){
    // mask contains info about used numbers
    if(pos==ans.size()) return true;
    if(ans[pos]!=-1) return help(ans,pos+1,mask);
    int n=(ans.size()+1)/2;
    for(int i=n;i>=1;i--){
        if(mask&(1<<(i-1))) continue;                                   //  since 'i' has been used before
        if((i!=1 && pos+i>=ans.size()) || (i!=1 && ans[pos+i]!=-1)) continue;      //  since I can't put 'i' now 
        if(i==1){
            ans[pos]=i;
            mask^=(1<<(i-1));
            if(help(ans,pos+1,mask)) return true;
            ans[pos]=-1;
            mask^=(1<<(i-1));
        }
        else{
            ans[pos]=i;
            ans[pos+i]=i;
            mask^=(1<<(i-1));
            if(help(ans,pos+1,mask)) return true;
            ans[pos]=-1;
            ans[pos+i]=-1;
            mask^=(1<<(i-1));
        }
    }
    return false;
}

vector<int> constructDistancedSequence(int n){
    vector<int> ans(2*n-1,-1);
    help(ans,0,0);
    return ans;
}
};
