/**
 * @param {number} n
 * @return {number[]}
 */
var constructDistancedSequence = function(n) {
    const table = new Array(1 + n).fill(true)
    table[0] = false
    let result = new Array(2 * n - 1).fill(0)


    function pairAvailable(fromIndex, tryMe) {
        if (tryMe === 1)    return result[fromIndex] === 0
        return result[fromIndex] === 0 && result[fromIndex + tryMe] === 0
    }

    let remaining = n, solved = false
    function solve(fromIndex) {
        if (fromIndex === result.length) {
            if (remaining === 0) {
                solved = true
            }
        }

        if (result[fromIndex]) {
            solve(1 + fromIndex)
            return
        }

        for (let tryMe = n; tryMe > 0; tryMe--) {
            if (!table[tryMe])  continue
            if (!pairAvailable(fromIndex, tryMe))    continue

            result[fromIndex] = tryMe
            if (tryMe !== 1)    result[fromIndex + tryMe] = tryMe
            table[tryMe] = false
            remaining--

            solve(1 + fromIndex)
            if (!solved) {
                result[fromIndex] = 0
                if (tryMe !== 1)    result[fromIndex + tryMe] = 0
                table[tryMe] = true
                remaining++
            }
        }
    }


    solve(0)
    return result
};
