public class Solution {
    public int[] ConstructDistancedSequence(int n) {
        int size = (2 * n) - 1;
        int[] outAry = new int[size];
        HashSet<int> visited = new HashSet<int>();
        this.Construct(outAry, visited, 0, n, n);
        return outAry;
    }

    private bool Construct(int[] outAry, HashSet<int> visited, int startIdx, int startVal, int n)
    {
        if (startIdx > outAry.Length || startVal < 1)
        {
            return true;
        }

        // try different values to put in the next index
        int i = startVal;
        bool isSequencePossible;
        while (i >= 1)
        {
            outAry[startIdx] = i;
            if (i == 1)
            {                
                visited.Add(i);
                isSequencePossible = this.Construct(outAry, visited, this.GetNextAvailableIdx(startIdx, outAry), this.GetNextHighestUnvisited(visited, n), n);
                if (isSequencePossible)
                {
                    return true;
                }
                else
                {
                    visited.Remove(i);
                    outAry[startIdx] = 0;
                }

                break;
            }

            if ((startIdx + i) >= outAry.Length)
            {
                outAry[startIdx] = 0;
                return false;
            }

            if (outAry[startIdx + i] != 0)
            {
                outAry[startIdx] = 0;
                i = this.GetNextHighestUnvisited(visited, i - 1);
                continue;
            }

            outAry[startIdx + i] = i;
            visited.Add(i);
            isSequencePossible = this.Construct(outAry, visited, this.GetNextAvailableIdx(startIdx, outAry), this.GetNextHighestUnvisited(visited, n), n);
            if (isSequencePossible)
            {
                return true;
            }

            outAry[startIdx] = 0;
            outAry[startIdx + i] = 0;
            visited.Remove(i);
            i = this.GetNextHighestUnvisited(visited, i - 1);            
        }

        return false;
    }

    private int GetNextHighestUnvisited(HashSet<int> visited, int n)
    {
        int i = n;
        while (i >= 1 && visited.Contains(i))
        {
            i--;
        }

        return i;
    }

    private int GetNextAvailableIdx(int startIdx, int[] outAry)
    {
        int i = startIdx + 1;
        while (i < outAry.Length && outAry[i] != 0)
        {
            i++;
        }

        return i;
    }
}
