class Solution:
    def constructDistancedSequence(self, n: int) -> List[int]:
        self.ans=[]
        temp=[None]*(n*2-1)
        self.helper(temp, [j for j in range(n, 0, -1)])
        return self.ans
    def helper(self, arr, num):
        if self.ans:
            return
        if not num:
            self.ans=[i for i in arr]
            return
        cur=next(i for i,j in enumerate(arr) if j==None)
        for k,j in enumerate(num):
            last=cur+j
            if j==1 and arr[cur]==None:
                arr[cur]=j
                self.helper(arr, num[:k]+num[k+1:])
                arr[cur]=None
            elif j!=1 and last<len(arr) and arr[cur]==arr[last]==None:
                arr[cur]=arr[last]=j
                self.helper(arr, num[:k]+num[k+1:])
                arr[cur]=arr[last]=None
