def backtrack(candidates, result, start = 0):
    if len(candidates) <= 0:
        return True

    if start >= len(result):
        return False

    if result[start] != -1:
        return backtrack(candidates, result, start + 1)

    for i in range(len(candidates)):
        num = candidates[i]
        dist = 0 if num == 1 else num

        if (start + dist) < len(result) and result[start + dist] == -1:
            result[start] = num
            result[start + dist] = num
            candidates.pop(i)

            if not backtrack(candidates, result, start):
                result[start] = -1
                result[start + dist] = -1
                candidates.insert(i, num)
                continue
            else:
                return True

