            if(dfs(v, i + 1, n)) return true;
            v[i] = 0;
            if(j > 1) v[j + i] = 0;
            memo[j] = false;
        }
        return false;
    }

    vector<int> constructDistancedSequence(int n) {
        vector<int> vec((2 * n) - 1, 0);
        dfs(vec, 0, n);
        return vec;
