#define V vector<int>

class Solution {
private:
	bool is_found { false };
	int full { 0 };
	int n { 0 };

	void f(V *res, int set, int idx) {

		if (is_found)
			return;
		if (idx >= res->size()) {
			if (set == full) {
				this->is_found = true;
			}
			return;
		}

		if (res->at(idx) != 0) {
			f(res, set, idx + 1);
			return;
		}

		bool is_fill = false;
		for (int i = n - 1; i >= 1; i--) {
			if ((set & (1 << (i - 1))) == 0) {

				if (i == 1
						|| (idx + i < res->size() && res->at(idx + i) == 0)) {
					is_fill = true;

					res->at(idx) = i;
					if (i != 1)
						res->at(idx + i) = i;
					f(res, set | (1 << (i - 1)), idx + 1);
					if (this->is_found)
						break;

					res->at(idx) = 0;
					if (i != 1)
						res->at(idx + i) = 0;
				}
			}

		}
		if (!is_fill)
			f(res, set, idx + 1);

	}

public:

	V constructDistancedSequence(int n) {
		if (n == 1)
			return {1};

		V res(2 * n - 1, 0);
		res[0] = n;
		res[n] = n;

		for (int i = 0; i < n; i++)
			full = full | (1 << i);

		int set = 0 | (1 << (n - 1));
		this->n = n;

		f(&res, set, 1);

		return res;

	}

};
```

