Time O(n^2) Space O(n)
```
class Solution {
    public int[] constructDistancedSequence(int n) {
        int[] res = new int[2 * n - 1];
        int[] nums = new int[n];
        // construct candidates: nums=[n, n-1, n-2, ..., 1]
        int i = 0;
        while (n > 0) {
            nums[i] = n;
            i++;
            n--;
        }
        dfs(res, 0, nums);
        return res;
    }

    private boolean dfs(int[] res, int index, int[] nums) {
        if (index == res.length) return true;
        if (res[index] != 0) return dfs(res, index + 1, nums);
        for (int i = 0; i < nums.length; i++) {
            int currNum = nums[i];
            if (currNum == 0 || (currNum != 1 && (index + currNum >= res.length || res[index + currNum] != 0))) continue;
            nums[i] = 0; // mark nums[i] as visited
            res[index] = currNum;
            if (currNum != 1) {
                res[index + currNum] = currNum;
            }
            if (dfs(res, index + 1, nums)) {
                return true;
            }

            // go back to previous state
            nums[i] = currNum;
            res[index] = 0;
            if (currNum != 1) {
                res[index + currNum] = 0;
            }
        }

        return false;
    }
}
