            if (dfs(res, index + 1, nums)) {
                return true;
            }

            // go back to previous state
            nums[i] = currNum;
            res[index] = 0;
            if (currNum != 1) {
                res[index + currNum] = 0;
            }
        }

        return false;
    }
}
