class Solution {
    public int[] constructDistancedSequence(int n) {
        int[] res = new int[n * 2 - 1];
        int mask = (1 << n) - 1;
        dfs(n, 0, res, 0, mask);
        return res;
    }

    private boolean dfs(int n, int j, int[] res, int state, int mask) {
        if (state == mask) {
            return true;
        }
        if (j == res.length) {
            return false;
        }
        if (res[j] != 0) {
            return dfs(n, j + 1, res, state, mask);
        }
        for (int k = n; k > 0; k--) {
            if ((state & (1 << (k - 1))) != 0) {
                continue;
            }
            if (k == 1) {
                res[j] = k;
                if (dfs(n, j + 1, res, state ^ (1 << (k - 1)), mask)) {
                    return true;
                }
                res[j] = 0;
            }
            else if (j + k < res.length && res[j + k] == 0) {
                res[j + k] = k;
                res[j] = k;
                if (dfs(n, j + 1, res, state ^ (1 << (k - 1)), mask)) {
                    return true;
                }
                res[j + k] = 0;
                res[j] = 0;
            }
        }
        return false;
    } 
}
