class Solution {

    boolean f(int[] arr, boolean[] chk, int i){
        if(i>=arr.length-1) return true;
        if(arr[i]!=0) return f(arr, chk, i+1);
        for(int j=chk.length-1; j>=1; j--){
            if(chk[j]) continue;
            int add = (j==1?0:j);
            if(i+add >= arr.length) continue;
            if(arr[i]==0 && arr[i+add]==0){
                chk[j]=true;
                arr[i]=arr[i+add]=j;
                if(f(arr, chk, i+1)) return true;
                else arr[i]=arr[i+add]=0;
                chk[j]=false;
            }
        }
        return false;
    }

    public int[] constructDistancedSequence(int n) {
        int[] arr = new int[n*2-1];
        f(arr, new boolean[n+1], 0);
        for(int i=0; i<arr.length; i++)
            if(arr[i]==0){
                arr[i]=1; break;
            }
        return arr;
    }
}
