class Solution {
public:

	int sz;
	int N;

	bool backtrack(int i, vector<int> &ans, vector<bool> &seen)
	{
		if(i == sz)
			return 1;

		if(ans[i] != 0)
			return backtrack(i+1, ans, seen);

		for(int num=N; num>=1; num--)
		{
			if(num == 1)
			{
				if(!seen[num])                
				{
					ans[i] = 1;
					seen[1] = 1;

					if(backtrack(i+1, ans, seen))
						return 1;

					ans[i] = 0;
					seen[1] = 0;
				}
			}

			else
			{
				if(!seen[num] && i+num < sz && ans[i+num] == 0)                
				{
					ans[i] = ans[i+num] = num;
					seen[num] = 1;

					if(backtrack(i+1, ans, seen))
						return 1;

					ans[i] = ans[i+num] = 0;
					seen[num] = 0;
				}
			}
		}

		return 0;
	}

	vector<int> constructDistancedSequence(int n) {
		sz = 1 + 2*(n-1);
		N = n;

		vector<int> ans(sz, 0);
		vector<bool> seen(n+1, 0);

		backtrack(0, ans, seen);

		return ans;
	}
};

