class Solution {
    public int[] constructDistancedSequence(int n) {
        int[] result = new int[(n-1)*2+1];
        boolean[] visited = new boolean[n+1];

        construct(0, result, visited);

        return result;
    }

    private boolean construct(int index, int[] result, boolean[] visited) {
        if(index == result.length) {
            return true;
        }

        //If resultant index is empty
        if(result[index] == 0) {
            //try to insert max num first (Lexicographically Largest)
            for(int n = visited.length-1; n >= 1; n--) { 
                //If n is not visited
                if(!visited[n]) {
                    if(n == 1) {
                        visited[n] = true;
                        result[index] = n;
                        //Go to the next index
                        if(construct(index+1, result, visited)) return true;
                    } else if(index+n < result.length && result[index+n] == 0) {
                        visited[n] = true;
                        result[index] = n;
                        result[index+n] = n;
                        //Go to the next index
                        if(construct(index+1, result, visited)) return true;
                        //Unvisit if find false
                        result[index+n] = 0;
                    }
                    //Unvisit if find false
                    visited[n] = false;
                    result[index] = 0;
                }
            }
        } else if(result[index] != 0) {
            return construct(index+1, result, visited);
        }

        return false;
    }
}


