class Solution:
    def constructDistancedSequence(self, n: int) -> List[int]:
        l = [None] * (2*n-1)
        filled = set()

        def can_fill(k, i):
            if k == 1:
                return l[i] == None
            return i+k < len(l) and l[i] == l[i+k] == None

        def _fill(k, i, m):
            if k == 1:
                l[i] = m
            else:
                l[i] = l[i+k] = m

        def fill(k, i):
            filled.add(k)
            _fill(k, i, k)

        def erase(k, i):
            filled.remove(k)
            _fill(k, i, None)


        def bt(i):
            if i == len(l):
                return True
            if l[i] is not None:
                return bt(i+1)

            for k in range(n, 0, -1):
                if k in filled or not can_fill(k, i):
                    continue

                fill(k, i)
                if bt(i+1):
                    return True
                erase(k, i)

        bt(0)
        return l
