class Solution:
	def constructDistancedSequence(self, n: int) -> List[int]:
		res = [0] * (n * 2 - 1)
		seen = set()
		def backtracking(i):
			if i == len(res):
				return True
			if res[i] != 0:
				return backtracking(i + 1)
			for j in range(n, 0, -1):
				if j == 1 and j not in seen:
					res[i] = j
					seen.add(j)
					if backtracking(i + 1):
						return True
					res[i] = 0
					seen.remove(j)
				elif j != 1 and i + j < len(res) and res[i + j] == 0 and j not in seen:
					res[i] = j
					res[i + j] = j
					seen.add(j)
					if backtracking(i + 1):
						return True
					res[i] = 0
					res[i + j] = 0
					seen.remove(j)
		backtracking(0)
		return res


class Solution {
	public int[] constructDistancedSequence(int n) {
		int[] res = new int[n * 2 - 1];
		Set<Integer> seen = new HashSet<>();
		backtracking(n, seen, 0, res);
		return res;
	}

	private boolean backtracking(int n, Set<Integer> seen, int i, int[] res){
		if (i == res.length){
			return true;
		}
		if (res[i] != 0){
			return backtracking(n, seen, i + 1, res);
		}
		for (int j = n; j > 0; j --){
			if (j == 1 && !seen.contains(j)){
				res[i] = j;
				seen.add(j);
				if (backtracking(n, seen, i + 1, res)){
					return true;
				}
				res[i] = 0;
				seen.remove(j);
			}
			else if (j != 1 && !seen.contains(j) && i + j < res.length && res[i + j] == 0){
				res[i] = j;
				res[i + j] = j;
				seen.add(j);
				if (backtracking(n, seen, i + 1, res)){
					return true;
				}
				res[i] = 0;
				res[i + j] = 0;
				seen.remove(j);
			}
		}
		return false;
	}
}

