class Solution {
    public int[] result;
    public HashSet<Integer> set;
    public boolean checkForStopping = false;

    public boolean dfs(int[] res, int start, int n) {
        if (set.size() == n) {
            result = Arrays.copyOf(res, res.length);
            checkForStopping = true;
            return false;
        }

        if (res[start] != 0) {
            start++;
        }

        for (int i = n; i > 0; i--) { 
            if (checkForStoping)
                return false;

            if (set.contains(i)) {
                continue;

            } else if (res[start] != 0) {
                i++;
                start++;
                continue;
            }

            if (i != 1) {     
                if (!(start + i < 2 * n - 1)) {
                    return false;
                }
                if (res[start + i] == 0) {
                    res[start + i] = i;
                } else {
                    continue;
                }
            }

            res[start] = i;
            set.add(i);

            if (!dfs(res, start + 1, n)) {
                res[start] = 0;

                if (i != 1) 
                    res[start + i] = 0;
                set.remove(i);
            }
        }
        return false;
    }

    public int[] constructDistancedSequence(int n) {
        if (n == 1) {
            int[] res = new int[1];
            res[0] = 1;
            return res;
        }

        int[] res = new int[2 * n - 1];
        max = new int[2 * n - 1];
        set = new HashSet<>();

        dfs(res, 0, n);

        return max;
    }
}
