class Solution {
    public int[] constructDistancedSequence(int n) {
        int len = 2 * n - 1;
        int[] a = new int[len];
        int[] f = new int[n]; // used nums

        rec(a, 0, len, f);

        return a;
    }

    private boolean rec(int[] a, int i, int len, int[] f) {
        while(i < len && a[i] != 0)
            i++;
        if(i == len)
            return true;

        for (int i1 = f.length - 1; i1 >= 0; i1--) {
            if(f[i1] > 0)
                continue;
            int num = i1 + 1;
            if(num == 1 || (i + num < len && a[i + num] == 0)) {
                a[i] = num;
                if(num > 1) a[i + num] = num;
                f[i1] = 1;

                if(rec(a, i + 1, len, f))
                    return true;

                a[i] = 0;
                if(num > 1) a[i + num] = 0;
                f[i1] = 0;
            }
        }

        return false;
    }

}
