        if (ans[idx] != -1){
            rec(idx + 1, ub, bound);
            return ;
        }
        for(int i = ub ; i > 0 ; i--){
            if (used[i]) continue ;
            if (i != 1 && i + idx >= bound) continue ;
            if (i != 1 && ans[i + idx] != -1 ) continue ;
            if (i == 1){
                ans[idx] = 1;
                used[1] = 1;
                rec(idx + 1, ub, bound);
                if (flag) return ;
                used[1] = 0;
                ans[idx] = -1;
                continue ;
            }

            ans[idx] = i;
            ans[idx + i] = i;
            used[i] = 1;
            rec(idx + 1, ub, bound);
            if (flag) return ;
            ans[idx] = -1;
            ans[idx + i] = -1;
            used[i] = 0;
        }
    }
    vector<int> constructDistancedSequence(int n) {
        flag = 0;
        used = vector<int>(n + 1,0);
        ans = vector<int>(n * 2 - 1, -1);
        rec(0,n,2 * n - 1);
        return ans;
    }
};

